INSERT INTO public.authors (id, name, biography, birth_date, death_date) VALUES (4, 'Jules Verne', 'Jules Verne, né le 8 février 1828 à Nantes et mort le 24 mars 1905 à Amiens, est un écrivain français dont l''œuvre est, pour la plus grande partie, constituée de romans d''aventures évoquant les progrès scientifiques du XIXᵉ siècle.', '1828-02-08', '1905-03-25');
INSERT INTO public.authors (id, name, biography, birth_date, death_date) VALUES (5, 'J. K. Rowling', 'Joanne Rowling, également connue sous le nom de J. K. Rowling et le pseudonyme de Robert Galbraith, est une romancière et scénariste britannique née le 31 juillet 1965 dans l’agglomération de Yate, dans le Gloucestershire, en Angleterre.', '1965-07-31', null);