INSERT INTO public.books (id, author_id, name, summary, release_date, available, book_reference) VALUES (11, 4, 'L’Île mystérieuse', 'L’Île mystérieuse est un roman de Jules Verne, paru en 1875. Il constitue une suite à Vingt mille lieues sous les mers ainsi qu’à Les Enfants du capitaine Grant, auxquels il est rattaché par des éléments narratifs.', '1875-12-15', true, '1865498135');
INSERT INTO public.books (id, author_id, name, summary, release_date, available, book_reference) VALUES (1, 5, 'Harry Potter à l''école des sorciers', 'Le jour de ses onze ans, Harry Potter, un orphelin élevé par un oncle et une tante qui le détestent, voit son existence bouleversée. Un géant nommé Hagrid,vient le chercher pour l''emmener à Poudlard, une école de sorcellerie ! Voler en balai, jeter des sorts, combattre les trolls : Harry se révèle un sorcier doué. Mais quel est le mystère qui l''entoure ? Et qui est l''effroyable V..., le mage dont personne n''ose prononcer le nom ?', '1997-06-26', false, '2070643026');
INSERT INTO public.books (id, author_id, name, summary, release_date, available, book_reference) VALUES (2, 5, 'Harry Potter et la Chambre des Secrets', 'Une rentrée fracassante en voiture volante, une étrange malédiction qui s''abat sur les élèves, cette deuxième année à l''école des sorciers ne s''annonce pas de tout repos ! Entre les cours de potions magiques, les matches de Quidditch et les combats de mauvais sorts, Harry Potter trouvera-t-il le temps de percer le mystère de la Chambre des Secrets ? Un livre magique pour sorciers confirmés.', '1998-07-02', false, '2070524558');
INSERT INTO public.books (id, author_id, name, summary, release_date, available, book_reference) VALUES (4, 5, 'Harry Potter et la coupe de feu', 'Après un horrible été chez les Dursley, Harry Potter entre en quatrième année au collège de Poudlard. A quatorze ans, il voudrait simplement être un jeune sorcier comme les autres, retrouver ses amis Ron et Hermione, assister avec eux à la Coupe du Monde: de Quidditch, apprendre de nouveaux sortilèges et essayer clos lotions inconnues. Une grande nouvelle l''attend à son arrivée : la tenue à Poudlard d''un tournoi de magie entre les plus célèbres écoles de sorcellerie. Déjà les spectaculaires délégations étrangères font leur entrée... Harry se réjouit. Trop vite. Il  va se trouver plongé au coeur des événements les plus dramatiques qu''il ait jamais eu à affronter. Envoûtant, drôle, bouleversant, ce quatrième tome est le pilier central clos aventures de Harry Potter.', '2000-07-08', true, '2070496548');
INSERT INTO public.books (id, author_id, name, summary, release_date, available, book_reference) VALUES (5, 5, 'Harry Potter et l''ordre du Phénix', 'A quinze ans, Harry s''apprête à entrer en cinquième année à Poudlard. Et s''il est heureux de retrouver le monde des sorciers, il n''a jamais été aussi anxieux. L''adolescence, la perspective des examens importants en fin d''année et ces étranges cauchemars... Car Celui-Dont-On-Ne-Doit-Pas-Prononcer-Le-Nom est de retour et, plus que jamais, Harry sent peser sur lui une terrible menace. Une menace que le ministère de la Magie ne semble pas prendre au sérieux, contrairement à Dumbledore. Poudlard devient alors le terrain d''une véritable lutte de pouvoir. La résistance s''organise autour de Harry qui va devoir compter sur le courage et la fidélité de ses amis de toujours... D''une inventivité et d''une virtuosité rares, découvrez le cinquième tome de cette saga que son auteur a su hisser au rang de véritable phénomène littéraire.', '2003-06-21', true, '2070358923');
INSERT INTO public.books (id, author_id, name, summary, release_date, available, book_reference) VALUES (6, 5, 'Harry Potter et le Prince de Sang-Mélé', 'Dans un monde de plus en plus inquiétant, Harry se prépare à retrouver Ron et Hermione. Bientôt, ce sera la rentrée à Poudlard, avec les autres étudiants de sixième année. Mais pourquoi le professeur Dumbledore vient-il en personne chercher Harry chez les Dursley? Harry, Ron et Hermione entrent en sixième année à Poudlard où ils vont vivre leur dernière année avant la majorité qui est fixée, chez les sorciers, à l’âge de dix-sept ans. Des événements particulièrement marquants vont contribuer à faire passer Harry du statut d’adolescent à celui d’homme. Ce tome, sur fond de guerre contre un Voldemort plus puissant que jamais, se révèle plus sombre que les précédents. Secrets, alliances et trahisons conduisent aux événements les plus dramatiques qu’Harry ait eu à affronter.', '2005-07-16', true, '2070258945');
INSERT INTO public.books (id, author_id, name, summary, release_date, available, book_reference) VALUES (7, 5, 'Harry Potter et les Relique de La Mort', 'Harry a été chargé d''une sombre, dangereuse et apparemment impossible mission : celle de localiser et de détruire les Horcuxes   restant de Voldemort. Jamais Harry ne s''était senti aussi seul, jamais Harry n''avait du affronter un avenir aussi incertain. Mais il doit, d''une façon ou d''une autre, trouver le courage de porter le fardeau qui est sur ses épaules. Abandonnant la chaleur, la sûreté et la compagnie du Terrier, il doit suivre sans crainte ni hésitation le chemin inexorable qui se présente à lui... Dans ce dernier opus de la série des Harry Poter, J.K.Rowling dévoile les réponses tant attendues aux nombreuses questions des fans du sorcier anglais.', '2007-07-21', true, '2070181598');
INSERT INTO public.books (id, author_id, name, summary, release_date, available, book_reference) VALUES (8, 4, 'Le Tour du monde en quatre-vingts jours', 'Le Tour du monde en quatre-vingts jours est un roman d''aventures de Jules Verne, publié en 1872. Le roman raconte la course autour du monde d''un gentleman anglais, Phileas Fogg, qui a fait le pari d''y parvenir en quatre-vingts jours. Il est accompagné par Jean Passepartout, son serviteur français.', '1872-11-06', true, '1941546312');
INSERT INTO public.books (id, author_id, name, summary, release_date, available, book_reference) VALUES (9, 4, 'Vingt mille lieues sous les mers', 'Vingt mille lieues sous les mers est un roman d''aventures de Jules Verne, paru en 1869-1870. C''est le cinquième livre le plus traduit au monde. Il a fait l''objet de nombreuses adaptations au cinéma, à la télévision ainsi qu''en bande dessinée.', '1871-11-16', true, '1894946135');
INSERT INTO public.books (id, author_id, name, summary, release_date, available, book_reference) VALUES (10, 4, 'Voyage au centre de la Terre', 'Voyage au centre de la Terre est un roman d''aventures, écrit en 1864 par Jules Verne. Il fut publié en édition originale in-18 le 25 novembre 1864, puis en grand in-octavo le 13 mai 1867. Le texte de 1867 comporte deux chapitres de plus que celui de 1864', '1864-11-25', true, '1895656456');
INSERT INTO public.books (id, author_id, name, summary, release_date, available, book_reference) VALUES (12, 4, 'De la Terre à la Lune', 'De la Terre à la Lune, trajet direct en 97 heures 20 minutes est un roman d''anticipation de Jules Verne, paru en 1865. Il relate comment, après la fin de la guerre de Sécession, une association d''artilleurs et de scientifiques liés à l''industrie militaire tente d''envoyer sur la Lune un obus habité par trois hommes.', '1865-07-14', true, '1756423546');
INSERT INTO public.books (id, author_id, name, summary, release_date, available, book_reference) VALUES (14, 4, 'Cinq semaines en ballon', 'Cinq semaines en ballon est un roman de Jules Verne, paru en 1863.', '1863-01-31', true, '1658643325');
INSERT INTO public.books (id, author_id, name, summary, release_date, available, book_reference) VALUES (15, 4, 'Les Enfants du capitaine Grant', 'Les Enfants du capitaine Grant est un roman d''aventures de Jules Verne, paru en 1868.', '1868-06-23', true, '1488615443');
INSERT INTO public.books (id, author_id, name, summary, release_date, available, book_reference) VALUES (16, 4, 'Michel Strogoff', 'Michel Strogoff est un roman d''aventures historique de Jules Verne, paru en 1876.', '1876-12-15', true, '1946519784');
INSERT INTO public.books (id, author_id, name, summary, release_date, available, book_reference) VALUES (17, 4, 'Deux ans de vacances', 'Deux ans de vacances est un roman de Jules Verne paru en 1888.', '1888-12-15', true, '1485641354');
INSERT INTO public.books (id, author_id, name, summary, release_date, available, book_reference) VALUES (18, 4, 'Autour de la Lune', 'Autour de la Lune est un roman d’anticipation de Jules Verne, paru en 1869', '1870-01-13', true, '1568413466');
INSERT INTO public.books (id, author_id, name, summary, release_date, available, book_reference) VALUES (13, 4, 'Un capitaine de quinze ans', 'Un capitaine de quinze ans est un roman d''aventures de Jules Verne, paru en 1878. Le récit est essentiellement consacré à la question de l''esclavage, et plus particulièrement du commerce d''esclaves africains par d''autres Africains.', '1878-12-15', false, '1768546323');
INSERT INTO public.books (id, author_id, name, summary, release_date, available, book_reference) VALUES (19, 4, 'Paris au XXe siècle', 'Paris au XXᵉ siècle est un roman d''anticipation écrit par Jules Verne en 1860, mais paru seulement en 1994, à titre posthume.', '1860-08-13', true, '1889713456');
INSERT INTO public.books (id, author_id, name, summary, release_date, available, book_reference) VALUES (20, 4, 'Le Château des Carpathes', 'Dans le village de Werst, Frick, un berger, remarque un jour qu''une fumée semble sortir du château en ruine de Rodolphe de Gortz : l''édifice serait donc à nouveau habité. Cette nouvelle terrifie les villageois, persuadés que le château est hanté et que ce sont des fantômes qui sont venus l''occuper. Le jeune forestier Nick Deck et le médecin du village, qui l''accompagne, décident d''aller au château mais sont victimes de surprenants phénomènes. C''est à ce moment-là qu''arrive au village le jeune comte Franz de Télek. Il apprend la situation du château et est frappé par le nom de son propriétaire.', '1892-12-15', true, '1859987654');
INSERT INTO public.books (id, author_id, name, summary, release_date, available, book_reference) VALUES (21, 4, 'Le Phare du bout du monde', 'Le Phare du bout du monde est un roman de Jules Verne, paru en 1905.', '1905-11-15', true, '1320165879');
INSERT INTO public.books (id, author_id, name, summary, release_date, available, book_reference) VALUES (22, 4, 'Maître du monde', 'Maître du monde est un roman de Jules Verne, paru en 1904.', '1904-12-15', true, '1214589445');
INSERT INTO public.books (id, author_id, name, summary, release_date, available, book_reference) VALUES (3, 5, 'Harry Potter et le Prisonnier d''Azkaban', 'Sirius Black, le dangereux criminel qui s''est échappé de la forteresse d''Azkaban, recherche Harry Potter. C''est donc sous bonne garde que l''apprenti sorcier fait sa troisième rentrées. Au programme : des cours de divination, la fabrication d''une potion de ratatinage, le dressage des hippogriffes... Mais Harry est-il vraiment a l''abri? Un livre époustouflant qui vous emportera dans un tourbillon de surprise et d''émotion !', '1999-07-08', false, '2070514654');
INSERT INTO public.books (id, author_id, name, summary, release_date, available, book_reference) VALUES (23, 5, 'Harry Potter et l''ordre du Phénix', 'A quinze ans, Harry s''apprête à entrer en cinquième année à Poudlard. Et s''il est heureux de retrouver le monde des sorciers, il n''a jamais été aussi anxieux. L''adolescence, la perspective des examens importants en fin d''année et ces étranges cauchemars... Car Celui-Dont-On-Ne-Doit-Pas-Prononcer-Le-Nom est de retour et, plus que jamais, Harry sent peser sur lui une terrible menace. Une menace que le ministère de la Magie ne semble pas prendre au sérieux, contrairement à Dumbledore. Poudlard devient alors le terrain d''une véritable lutte de pouvoir. La résistance s''organise autour de Harry qui va devoir compter sur le courage et la fidélité de ses amis de toujours... D''une inventivité et d''une virtuosité rares, découvrez le cinquième tome de cette saga que son auteur a su hisser au rang de véritable phénomène littéraire.', '2003-06-21', true, '2070358923');
